var openPrompt = function(params) { 
	var buttons = {};
	
	if(params.submit != undefined) {
		buttons[params.title] = 1;
	} else {
		buttons[''] = 0; // Disable submit if the param submit is empty
	}

	var promptInfo = {
		state0: {
			title: params.title,
			html: params.response,
			uid: params.id,
			buttons: buttons,
			
			submit: function(e, v, m, f) { 
				e.preventDefault();
			}
		}
	}

	$.prompt(promptInfo);
}

